import logo from './logo.svg';
import './App.css';

import React from 'react';
import example1 from './example1.jpg';
import example2 from './example2.jpg';
import example3 from './example3.jpg';
import example4 from './example4.jpg';
import example5 from './example5.jpg';

class Homepage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      carouselImages: [
        example1,
        example2,
        example3,
        example4,
        example5
      ],
      currentImageIndex: 0
    };
  }

  handleNextImage = () => {
    const { carouselImages, currentImageIndex } = this.state;
    if (currentImageIndex < carouselImages.length - 1) {
      this.setState({ currentImageIndex: currentImageIndex + 1 });
    }
  }

  handlePrevImage = () => {
    const { currentImageIndex } = this.state;
    if (currentImageIndex > 0) {
      this.setState({ currentImageIndex: currentImageIndex - 1 });
    }
  }

  render() {
    const { carouselImages, currentImageIndex } = this.state;

    return (
      <div>
        <div className="banner">
          {/* Your banner content */}
        </div>

        <div className="carousel">
          <div className="carousel-inner">
            <button className="prev" onClick={this.handlePrevImage}>&#10094;</button>
            {carouselImages.map((image, index) => (
              <img key={index} src={image} alt={`Carousel Image ${index}`} className={index === currentImageIndex ? 'center' : ''} />
            ))}
            <button className="next" onClick={this.handleNextImage}>&#10095;</button>
          </div>
        </div>

        {/* Rest of your content */}
      </div>
    );
  }
}

export default Homepage;
