import React from 'react';
import './App.css';
import RecipeGrid from './RecipeGrid'; // Import the RecipeGrid component
import example1 from './example1.jpg';
import example2 from './example2.jpg';
import example3 from './example3.jpg';
import example4 from './example4.jpg';

class Homepage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      carouselImages: [
        { src: example1, name: 'Dish 1', cookTime: '30 minutes', calories: '200', link: 'https://dish1website.com' },
        { src: example2, name: 'Dish 2', cookTime: '40 minutes', calories: '250', link: 'https://dish2website.com' },
        { src: example3, name: 'Dish 3', cookTime: '25 minutes', calories: '180', link: 'https://dish3website.com' },
        { src: example4, name: 'Dish 4', cookTime: '35 minutes', calories: '220', link: 'https://dish4website.com' },
      ],
      hoveredImageIndex: null,
      recipes: [ // Recipe data for the grid
        { name: 'Recipe 1', cookTime: '45 minutes', calories: '300', image: 'recipe1.jpg' },
        { name: 'Recipe 2', cookTime: '35 minutes', calories: '250', image: 'recipe2.jpg' },
        { name: 'Recipe 3', cookTime: '60 minutes', calories: '400', image: 'recipe3.jpg' },
        { name: 'Recipe 4', cookTime: '20 minutes', calories: '180', image: 'recipe4.jpg' },
        // Add more recipe objects as needed
      ]
    };
  }

  handleHoverImage = (index) => {
    this.setState({ hoveredImageIndex: index });
  }

  render() {
    const { carouselImages, hoveredImageIndex, recipes } = this.state;

    return (
      <div>
        {/* Existing code */}
        {/* ... */}
        <div className="search-recipes">
          <h2>Search Recipes</h2>
          <div className="search-container">
            <input type="text" placeholder="Search..." />
            <button>Search</button>
          </div>
        </div>

        {/* Replace "Items" header with RecipeGrid component */}
        <div className="recipe-container">
          <h2>Recipes</h2>
          <RecipeGrid recipes={recipes} />
        </div>
      </div>
    );
  }
}

export default Homepage;
