import React from 'react';
import './App.css';
import example1 from './example1.jpg';
import example2 from './example2.jpg';
import example3 from './example3.jpg';
import example4 from './example4.jpg';

class Homepage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      carouselImages: [
        { src: example1, name: 'Dish 1', cookTime: '30 minutes', calories: '200' },
        { src: example2, name: 'Dish 2', cookTime: '40 minutes', calories: '250' },
        { src: example3, name: 'Dish 3', cookTime: '25 minutes', calories: '180' },
        { src: example4, name: 'Dish 4', cookTime: '35 minutes', calories: '220' },
      ],
      hoveredImageIndex: null
    };
  }

  handleHoverImage = (index) => {
    this.setState({ hoveredImageIndex: index });
  }

  render() {
    const { carouselImages, hoveredImageIndex } = this.state;

    return (
      <div>
        <div className="banner">
          <div className="logo">
            <img src="logo.png" alt="Logo" />
          </div>
          <h1 className="title">Fridge2Food</h1>
          <nav className="ribbon">
            <ul>
              <li><a href="/login">Login</a></li>
              <li><a href="/my-fridge">My Fridge</a></li>
            </ul>
          </nav>
        </div>

        <div className="carousel">
          <div className="carousel-inner">
            {carouselImages.map((imageData, index) => (
              <div
                key={index}
                className="image-container"
                onMouseEnter={() => this.handleHoverImage(index)}
                onMouseLeave={() => this.handleHoverImage(null)}
              >
                <img
                  src={imageData.src}
                  alt={`Carousel Image ${index}`}
                  className={index === hoveredImageIndex ? 'center grow' : ''}
                />
                {index === hoveredImageIndex && (
                  <div className="overlay">
                    <div className="text">
                      <p>{imageData.name}</p>
                      <p>Cook Time: {imageData.cookTime}</p>
                      <p>Calories: {imageData.calories}</p>
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        <div className="search-recipes">
          <h2>Search Recipes</h2>
          <div className="search-container">
            <input type="text" placeholder="Search..." />
            <button>Search</button>
          </div>
        </div>

        <div className="item-list">
          <h2>Items</h2>
          {/* Your items list content */}
        </div>
      </div>
    );
  }
}

export default Homepage;
